package javapratices;

public class SwitchSt1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String fruit = "Mango";
		switch (fruit) {
		case "Banana":
			System.out.println("fruit is Banana");
			break;
			
		case "Mango":
			System.out.println("fruit is Mango");
			break;
			
		case "Cherry":
			System.out.println("fruit is Cherry");
			break;
			
		case "pineapple":
			System.out.println("fruit is pineapple");
			break;
			
			default:
				System.out.println("fruit is not edible");
				break;
				


			


			

		}
		 
		
		
		
		
			

	}

}
